var TinyMCE_SImagePlugin = {getInfo : function() {return {longname : 'Simple image upload plugin',author : 'Sergey Galashyn',authorurl : 'http://ziost.com',infourl : 'http://ziost.com',version : "1.0"};},getControlHTML : function(cn) {switch (cn) {case "simage":return tinyMCE.getButtonHTML(cn, 'lang_simage_desc', '{$pluginurl}/images/simage.gif', 'mceSImage', true);}return "";},execCommand : function(editor_id, element, command, user_interface, value) {switch (command) {case "mceSImage":var simage = new Array();simage['width'] = 360;simage['height'] = 110;simage['file'] = tinyMCE.getParam("plugin_simage_action");simage['path'] = tinyMCE.getParam("plugin_simage_path");tinyMCE.openWindow(simage, {editor_id : editor_id});tinyMCE.triggerNodeChange(false);return true;}return false;}};tinyMCE.addPlugin("simage", TinyMCE_SImagePlugin);